import React, { Component } from "react";
import { withRouter } from "react-router-dom";

class SignUp extends Component {
  constructor(props) {
    super(props);
    const localUser = localStorage.getItem("user");
    if (!localUser) {
      this.props.history.push("/login");
    } else if (!this.props.location.state) {
      this.props.history.push("/home");
    }

    this.state = {
      user: {
        firstName: "",
        lastName: "",
        dob: "",
        email: "",
        password: "",
        confirmPassword: "",
        phone: "",
        gender: "",
      },
      errors: {},
    };
  }

  handleSubmit = (e) => {
    e.preventDefault();
    const { user } = this.state;
    Object.keys(user).forEach((field) =>
      this.validateProperty(field, user[field])
    );
    if (!Object.keys(this.state.errors).length) {
      this.props.addUser(this.state.user);
    }
  };

  componentDidMount() {
    const user = JSON.parse(localStorage.getItem("user"));
    this.setState({ user });
  }

  render() {
    const { user } = this.state;
    return (
      <>
        <h1 className="my-3">SIGN UP</h1>
        <form onSubmit={this.handleSubmit}>
          <div className="form-group">
            <label htmlFor="firstName">First Name*</label>
            <input
              type="text"
              name="firstName"
              id="firstName"
              required
              value={user.firstName}
              onChange={(e) =>
                this.validateProperty(e.target.name, e.target.value)
              }
              className="form-control"
            />
            <small className="text-sm text-danger">
              {this.state.errors["firstName"]}
            </small>
          </div>

          <div className="form-group">
            <label htmlFor="lastName">Last Name*</label>
            <input
              type="text"
              name="lastName"
              id="lastName"
              required
              value={user.lastName}
              onChange={(e) =>
                this.validateProperty(e.target.name, e.target.value)
              }
              className="form-control"
            />
            <small className="text-sm text-danger">
              {this.state.errors["lastName"]}
            </small>
          </div>
          <div className="form-group">
            <label htmlFor="phone">Phone Number*</label>
            <input
              type="tel"
              name="phone"
              id="phone"
              required
              value={user.phone}
              onChange={(e) =>
                this.validateProperty(e.target.name, e.target.value)
              }
              className="form-control"
            />
            <small>Format: 1234567890</small>
            <br />
            <small className="text-sm text-danger">
              {this.state.errors["phone"]}
            </small>
          </div>
          <div className="form-group">
            <label htmlFor="gender">Gender*</label>
            <select
              name="gender"
              id="gender"
              required
              value={user.gender}
              className="form-control"
              onChange={(e) =>
                this.validateProperty(e.target.name, e.target.value)
              }
            >
              <option></option>
              <option value="male">Male</option>
              <option value="female">Female</option>
            </select>
            <small className="text-sm text-danger">
              {this.state.errors["gender"]}
            </small>
          </div>
          <div className="form-group">
            <label htmlFor="dob">Date Of Birth*</label>
            <input
              type="date"
              name="dob"
              id="dob"
              required
              max="2022-01-01"
              min="1920-01-01"
              value={user.dob}
              onChange={(e) =>
                this.validateProperty(e.target.name, e.target.value)
              }
              className="form-control"
            />
            <small className="text-sm text-danger">
              {this.state.errors["dob"]}
            </small>
          </div>

          <div className="form-group">
            <label htmlFor="email">Email*</label>
            <input
              type="email"
              name="email"
              autoComplete="off"
              id="email"
              required
              value={user.email}
              onChange={(e) =>
                this.validateProperty(e.target.name, e.target.value)
              }
              className="form-control"
            />
            <small className="text-sm">
              Email must be example@example.example
            </small>
            <br />
            <small className="text-sm text-danger">
              {this.state.errors["email"]}
            </small>
          </div>

          <div className="form-group">
            <label htmlFor="password">Password*</label>
            <input
              name="password"
              id="password"
              required
              value={this.state.user.password}
              onChange={(e) =>
                this.validateProperty(e.target.name, e.target.value)
              }
              className="form-control"
            />
            <small className="text-sm">
              Password must be at least 8 characters long and must contains 1
              lowercase character, 1 uppercase character, 1 numeric character
              and 1 special character !@#$%^&*.
            </small>
            <br />
            <small className="text-sm text-danger">
              {this.state.errors["password"]}
            </small>
          </div>

          <div className="form-group">
            <label htmlFor="confirmPassword">Confirm Password*</label>
            <input
              name="confirmPassword"
              id="confirmPassword"
              required
              value={this.state.user.confirmPassword}
              onChange={(e) =>
                this.validateProperty(e.target.name, e.target.value)
              }
              className="form-control"
            />
            <small className="text-sm text-danger">
              {this.state.errors["confirmPassword"]}
            </small>
          </div>

          <button className="btn btn-primary">Sign Up</button>
        </form>
        <form onSubmit={this.handleGenerate}>
          <button className="btn btn-success recommend-password-button">
            Recomend Password
          </button>
        </form>
      </>
    );
  }
}

export default withRouter(SignUp);
